function initialize() {

  var BodyComponent = ng.core.Component({
    selector: 'body',
    template: '<router-outlet></router-outlet>'
  }).Class({
    constructor: function() {}
  });

  var MainModule = ng.core.NgModule({
      imports: [
        ng.platformBrowser.BrowserModule,
        ng.forms.FormsModule,
        Router()
      ],
      declarations: [
        BodyComponent,
        MainLayoutComponent(),
        HomePageComponent(),
        AboutPageComponent(),
        AccordionPageComponent()
      ],
      bootstrap: [BodyComponent],
      providers: []
    })
    .Class({
      constructor: function() {}
    });

  ng.platformBrowserDynamic
    .platformBrowserDynamic()
    .bootstrapModule(MainModule);
}
document.addEventListener('DOMContentLoaded', initialize);

function Router() {
  window.location.hash = 'app';
  return ng.router.RouterModule.forRoot([{
    path: 'app',
    redirectTo: 'home',
    pathMatch: 'full'
  }, {
    path: '',
    component: MainLayoutComponent(),
    children: [{
      path: 'home',
      component: HomePageComponent()
    },{
      path: 'about',
      component: AboutPageComponent()
    },{
      path: 'accordion',
      component: AccordionPageComponent()
    }]
  }], {
    useHash: true
  });
}

function MainLayoutComponent() {
  return ng.core.Component({
    templateUrl: "src/layout/main-layout/main-layout.html"
  }).Class({
    constructor: function() {
      this.root = {};
      this.root.appName = "Framework7 Angular2";
    }
  });
}

function AboutPageComponent() {
  return ng.core.Component({
    templateUrl: "src/pages/about/about.html"
  }).Class({
    constructor: function() {

    }
  });
}

function AccordionPageComponent() {
  return ng.core.Component({
    templateUrl: "src/pages/accordion/accordion.html"
  }).Class({
    constructor: function() {

    }
  });
}

function HomePageComponent() {
  return ng.core.Component({
    templateUrl: "src/pages/home/home.html"
  }).Class({
    constructor: function() {
      this.title = "Framework7 Angular2";
      this.userName = "test";
    }
  });
}
